import React , {useEffect, useState} from 'react';
import { useParams} from "react-router-dom";
import OrderFinder from '../apis/OrderFinder';
import EmailPoster from '../apis/EmailPoster';

const Tap = () => {
    const {id} = useParams();

    //from server response
    const [items, setItems] = useState([]);
    const [acctID, setAcctId] = useState("");
    const [name, setName] = useState("");
    const [successURL, setSuccessURL] = useState("");
    const [returnURL, setReturnURL] = useState("");
    const [subtotal, setSubtotal] = useState(0);
    const [shipping, setShipping] = useState(0);
    const [taxes, setTaxes] = useState(0);
    const [total, setTotal] = useState(0);



    //from customer
    const [email, setEmail] = useLocalStorage('email', '');
    const [emailEntered, setEmailEntered] = useLocalStorage('emailEntered', false);

    //from stripe
    const [billing, setBilling] = useState([]);
    const [billingExists, setBillingExists] = useLocalStorage('billingExists', false);




    useEffect( () => {

        const fetchData = async () => {
        try {
            const response = await OrderFinder.get("/"+id);
            setItems(response.data.data.items)
            setAcctId(response.data.data.order[0].acctid);
            setName(response.data.data.order[0].storename);
            setSubtotal(response.data.data.order[0].subtotal);
            setShipping(response.data.data.order[0].shipping);
            setTaxes(response.data.data.order[0].taxes);
            setTotal(response.data.data.order[0].total);
            setSuccessURL(response.data.data.order[0].successURL);
            setReturnURL(response.data.data.order[0].returnurl);
         } catch(err){
           console.log(err)
         }
         try{
            const response = await EmailPoster.post("/", {
                email
            })
            if(response.data.data.list.data[0].name != null){
                setBilling([
                    response.data.data.list.data[0]
                ]);
                setBillingExists(true);
            }

        } catch(err){
            console.log(err)
        }
       }
       fetchData();

    },[]);



    const handleEmailSet = async () => {
        setEmail(email)
        setEmailEntered(true)
        console.log("email set"+email)
        try{
            const response = await EmailPoster.post("/", {
                email
            });
            console.log(response.data.data.list.data[0])
            console.log(response.data.data.list.data[0].name.length)
            if(response.data.data.list.data[0].name != null){
                setBilling([
                    response.data.data.list.data[0]
                ]);
                setBillingExists(true);
            }

        } catch(err){

        }


    };

    const editEmail = async () => {
        console.log("clear email")
            setEmailEntered(false)
            setEmail("")
            setBilling([])
    };

    // Hook
function useLocalStorage(key, initialValue) {
    // State to store our value
    // Pass initial state function to useState so logic is only executed once
    const [storedValue, setStoredValue] = useState(() => {
      try {
        // Get from local storage by key
        const item = window.localStorage.getItem(key);
        // Parse stored json or if none return initialValue
        return item ? JSON.parse(item) : initialValue;
      } catch (error) {
        // If error also return initialValue
        console.log(error);
        return initialValue;
      }
    });

    // Return a wrapped version of useState's setter function that ...
    // ... persists the new value to localStorage.
    const setValue = value => {
      try {
        // Allow value to be a function so we have same API as useState
        const valueToStore =
          value instanceof Function ? value(storedValue) : value;
        // Save state
        setStoredValue(valueToStore);
        // Save to local storage
        window.localStorage.setItem(key, JSON.stringify(valueToStore));
      } catch (error) {
        // A more advanced implementation would handle the error case
        console.log(error);
      }
    };
    return [storedValue, setValue];
  }

  function isNumeric(n) {
    return !isNaN(parseInt(n)) && isFinite(n);
}

    return (
        <>

        <div className="text-center">
            <h1>{name}</h1>
        </div>


        <div className="row ">
            <div className="col-sm-12">


            {emailEntered ? 
            <>
            <div className="card mt-4 mb-4 ">
                <div className="card-body">

                    <div className="row mt-4">
                        <div className="col">email</div>
                        <div className="col"><span>{email}</span></div>
                        <div className="col text-right"><button className="btn btn-link p-0 m-0 d-inline align-baseline"onClick={editEmail}>edit</button></div>
                    </div> 

                    <div className="row mt-4">
                        <div className="col">Bill to</div>

                        <div className="col"><span>{JSON.stringify(billing[0])}</span></div>
                        <div className="col"><span>{}</span></div>
                        <div className="col text-right"><button className="btn btn-link p-0 m-0 d-inline align-baseline" >edit</button></div>
                    </div> 
                </div>    
            </div>
            </>
               :

              <div className="card">

                    <div className="card-body">
                        <h5 className="card-title">Quick checkout</h5>
                        <p>Enter your email address to log in or sign up.</p>
                        <div className="">

                            <input 
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Email address"
                            type="text" 
                            className="mt-2 mb-2 form-control" 
                            />
                            <button onClick={handleEmailSet} className="mt-2 mb-2 btn btn-primary btn-block">Continue with TapPay</button>   
                        </div>
                    </div>

                </div> 
                }


                <div className="mt-4 text-nowrap card">
                    <div className="card-header">
                        <h4  className="text-center">Order Details</h4>
                    </div>
                                <ul className="list-group list-group-flush">
                    {items.map(item=>{
                 return(  
                                <li className="list-group-item">
                                    <div className="row">
                                    <div className="col">{item.name}</div> 
                                    <div className="col text-right">{item.price}</div>
                                    </div>
                                </li>
                    )
             })}
                     </ul>
                     <div className="card-body">
                        <ul className="list-group list-group-flush">
                            <li className="list-group-item">
                                <div className="row">
                                <div className="col">Subtotal</div> 
                                <div className="col text-right">{subtotal}</div>
                                </div>
                            </li>
                            <li className="list-group-item">
                                <div className="row">
                                <div className="col">Shipping</div> 
                                <div className="col text-right">{shipping}</div>
                                </div>
                            </li>
                            <li className="list-group-item">
                                <div className="row">
                                <div className="col">Taxes</div> 
                                <div className="col text-right">{taxes}</div>
                                </div>
                            </li>
                            <li className="list-group-item">
                                <div className="row">
                                <div className="col">Total</div> 
                                <div className="col text-right">{total}</div>
                                </div>
                            </li>
                        </ul>

                    </div>

            </div>

            <div className="mt-4 text-center"><a href={returnURL} className="btn btn-link p-0 m-0 d-inline align-baseline">Back</a></div>
            </div>

          </div>

        </>
    )
}

export default Tap;
