import React , {useEffect, useState} from 'react';
import { useParams} from "react-router-dom";


const Tap = () => {
    const {id} = useParams();
    const [items, setItems] = useState([]);
    const [acctID, setAcctId] = useState("");

    useEffect( () => {
        var decodedStringAtoB = atob(id);
        var body = JSON.parse(decodedStringAtoB);

        setItems(body.items)
        setAcctId(body.acctID);
        console.log(body.items)
        console.log(body.acctID)

       },[]);


    return (
        <>

             <div><h1>TapPay</h1></div>
             <h2>Order Details</h2>

             <div className="ml-4 mr-4 mt-4  d-flex flex-row justify-content-center">
                <form action="">
                <div className="card">
                    <div class="card-header">
                        <h4  className="text-center">Order Details</h4>
                    </div>
                    {items.map(item=>{
                 return(  
                        <ul class="list-group list-group-flush">
                            <li class="list-group-item">{item.name} <p>{item.price}</p></li>
                        </ul>





                    )
             })}

                </div>
            </form>
            </div>








        </>
    )
}

export default Tap;
