import React , {useEffect, useState} from 'react';
import { useParams} from "react-router-dom";


const Tap = () => {
    const {id} = useParams();
    const [items, setItems] = useState([]);
    const [acctID, setAcctId] = useState("");

    useEffect( () => {
        var decodedStringAtoB = atob(id);
        var body = JSON.parse(decodedStringAtoB);

        setItems(body.items)
        setAcctId(body.acctID);
        console.log(body.items)
        console.log(body.acctID)

       },[]);


    return (
        <>

             <div><h1>TapPay</h1></div>
             <h2>Order Details</h2>
             {items.map(items=>{
                 return(  
                     <div>
                        <p>{items.name}</p>
                        <p>{items.price}</p>
                     </div>

                    )
             })}






        </>
    )
}

export default Tap;
