import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom"
import "./App.css";
import {
  CardElement,
  useStripe,
  useElements
} from "@stripe/react-stripe-js";

const CheckoutForm= (props) => {
  const [succeeded, setSucceeded] = useState(false);
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState('');
  const [disabled, setDisabled] = useState(true);
  const [clientSecret, setClientSecret] = useState('');
  const stripe = useStripe();
  const elements = useElements();
  let history = useHistory(); 

  useEffect(() => {
    window.scrollTo(0, 0);
    // Create PaymentIntent as soon as the page loads

  }, []);



  const cardStyle = {
    style: {
      base: {
        color: "#32325d",
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: "antialiased",
        fontSize: "16px",
        "::placeholder": {
          color: "#32325d"
        }
      },
      invalid: {
        color: "#fa755a",
        iconColor: "#fa755a"
      }
    }
  };
  const handleChange = async (event) => {

    // Listen for changes in the CardElement
    // and display any errors as the customer types their card details
    setDisabled(event.empty);
    setError(event.error ? event.error.message : "");
  };

const paymentIntent = async (e) => {

  e.preventDefault();
  console.log("created payment intent")
  window
      .fetch("https://productroles.herokuapp.com/create-payment-intent", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({items: [{ price: props.price }]})
      })
      .then(res => {

        return res.json();

      })
      .then(data => {

        console.log("payment intent created"+data.clientSecret)
        handleSubmit(data.clientSecret);
      });
}

  const handleSubmit = async (clientSecret) => {
    console.log("confirming payment")
    setProcessing(true);
    const payload = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement)
      }
    });
    if (payload.error) {
      setError(`Payment failed ${payload.error.message}`);
      setProcessing(false);
    } else {

      setError(null);
      setProcessing(false);
      setSucceeded(true);
      handleForm();
    }
  };

  const goHome = () => {
    history.push("/");
  }
  const handleForm = () => {

        props.form();
  }
  return (

    <form id="payment-form" onSubmit={paymentIntent}>
      <CardElement id="card-element" options={cardStyle} onChange={handleChange} />
    </form>

  );
}

export default CheckoutForm
