import React , {Component, useEffect, useState} from 'react';
import NewOrder from '../apis/NewOrder';
import Onboard from '../apis/Onboard';
import { useHistory } from "react-router-dom";

const Home = () => {
    let history = useHistory();

    const sampleOrder = async() =>{
        console.log("send request to server and redirect automatically");
        try {
        const response = await NewOrder.post("/", {
                items : [

                    {
                        name: "Coffee",
                        price : 500
                    }
                    ],
                subtotal: 500,
                shipping: 0,
                taxes: 0,
                total: 500,
                storename: "Donate a coffee to the developer",
                acctID: "acct_1IXBaIICjuu6vzYp",
                returnURL : "https://cardlist.co",
                successURL : "https://cardlist.co"
        });
        console.log(response)
        window.open(response.data.data.redirect);
    }
    catch(err){
        console.log(err);
    }


    }

    const setupPayouts = async()=>{
        console.log("stripe onboarding");
        const response = await Onboard.post("/", {});
        console.log(response)
    }
    return (
        <>
            <div className="text-center">
                <h2>CardList</h2>
                <h5>Increase conversion rates and checkout time by letting customers save their card data in Stripe.</h5>
                <br></br>

                <p>Integrating with CardList is seamless! Simply send a request to our server with your customer's order details, redirect them to our checkout page, and we'll let them checkout with a new card or a payment method they've stored during previous purchases. </p>
                <br></br>

                <p>Click the button below to generate a sample order and browse the app's functionality. Feel free to actually buy me a coffee :) </p>
                <button  onClick={sampleOrder} className="btn btn-primary  btn-small">Donate a coffee</button>
                <br></br>
                <br></br>

                </div>

                <div className="text-center">
                <h4>Integration Steps</h4>

                <h5>We use Stripe to enable your customers to save payment information securely. 
                    </h5>
                    <h6>Just complete a simple Stripe Connect onbaording flow and you'll be ready to use CardList!</h6>
                    <button  onClick={setupPayouts} className="btn btn-primary  btn-small">Setup Payouts on Stripe</button>
                    <br></br>
                </div>
                <div className="text-center pb-4">
                    <p>Once you have your Stripe Connected AcctID, click below to open the Postman request for creating new CardList orders</p>
                    <a href={"https://app.getpostman.com/run-collection/e84dc45f266ef6e49341"} className="btn btn-link p-0 m-0 d-inline align-baseline">Postman Collection</a>

                </div>


            <div>
            </div>
        </>


    )
}

export default Home
