import React from 'react'
import { useHistory } from "react-router-dom"
const Header = () => {
    let history = useHistory()
    const handleAddJob = () => {
        history.push(`/add-job`)
    };
    return (
        <>
         <div class="row justify-content-center">
             <div class="col-4 m-3"><h4 className="text-center">Product Hiring </h4></div>
             <div class="col-5 mt-3 "><button onClick={(e)=> handleAddJob(e)} className="btn btn-warning ">Post a job</button></div>
           </div>


        </>
    )
}

export default Header;
