import React , {useEffect, useState} from 'react';
import { useParams} from "react-router-dom";


const Tap = () => {
    const {id} = useParams();
    const [items, setItems] = useState([]);
    const [acctID, setAcctId] = useState("");

    useEffect( () => {
        var decodedStringAtoB = atob(id);
        var body = JSON.parse(decodedStringAtoB);

        setItems(body.items)
        setAcctId(body.acctID);
        console.log(body.items)
        console.log(body.acctID)

       },[]);


    return (
        <>

        <div class="text-center">
            <h1>"brand name"</h1>
        </div>


            <div class="d-flex flex-row justify-content-center">


                <div className="card w-75">
                <form action="">  
                    <div class="card-body">
                        <h5 class="card-title">Quick checkout</h5>
                        <p>Enter your email address to log in or sign up.</p>
                        <div className="col">

                            <input 
                            placeholder="Email address"
                            type="text" 
                            className="mt-2 mb-2 form-control" 
                            />
                            <button className="mt-2 mb-2 btn btn-primary btn-block">Continue with TapPay</button>   
                        </div>
                    </div>
                    </form>
                </div>

                <div className="mt-4 text-nowrap card w-75 ">
                    <div class="card-header">
                        <h4  className="text-center">Order Details</h4>
                    </div>
                                <ul class="list-group list-group-flush">
                    {items.map(item=>{
                 return(  
                                <li class="list-group-item">
                                    <div class="row">
                                    <div class="col">{item.name}</div> 
                                    <div class="col text-right">{item.price}</div>
                                    </div>
                                </li>
                    )
             })}
                     </ul>
                    </div>

            </div>











        </>
    )
}

export default Tap;
