import React, { useState, useEffect } from "react";


const OTP = () => {
  const [otp, setOtp] = useState(new Array(6).fill());

  return (
    <div className="row">
            <div className="col text-center">
                <p>Enter the OTP in the field below to verify your phone number</p>

                {
                    otp.map((data,index) => {
                        return <input
                        className="otp-field"
                        type="text"
                        name="otp"
                        maxLength="1"
                        key={index}
                        value={data}
                        />
                    })
                }
            </div>

    </div>
  );
};

export default OTP;
