import React , {useEffect, useState} from 'react';
import { useParams} from "react-router-dom";


const Tap = () => {
    const {id} = useParams();
    const [items, setItems] = useState([]);
    const [acctID, setAcctId] = useState("");

    useEffect( () => {
        var decodedStringAtoB = atob(id);
        var body = JSON.parse(decodedStringAtoB);

        setItems(body.items)
        setAcctId(body.acctID);
        console.log(body.items)
        console.log(body.acctID)

       },[]);


    return (
        <>

             <div class="text-center">
                 <h1>TapPay</h1>
                 <h2>Order Details</h2>
                 </div>


             <div className="text-nowrap d-flex flex-row justify-content-center">
                <form action="">
                <div className="card">
                    <div class="card-header">
                        <h4  className="text-center">Order Details</h4>
                    </div>
                                <ul class="list-group list-group-flush">
                    {items.map(item=>{
                 return(  
                                <li class="list-group-item">
                                    <div class="row">
                                    <div class="col">{item.name}</div> 
                                    <div class="col text-right">{item.price}</div>
                                    </div>
                                </li>
                    )
             })}
                     </ul>
                    </div>

            </form>
            </div>








        </>
    )
}

export default Tap;
