import React , {Component, useEffect, useState} from 'react';
import { CodeBlock,dracula} from "react-code-blocks";


const Home = () => {

    return (
        <>
            <div className="text-center">
                <h1>CardList</h1>
                <br></br>
                <br></br>
                <h3>Increase conversion rates and let your customers checkout faster by saving and their card data in Stripe.</h3>
                <br></br>
                <br></br>
                <p>Integrating with CardList is seamless! Simply send a request to our server with your customer's order details (example shown below), redirect them to our checkout page, and we'll let them checkout with a new card or a payment method they've stored during previous purchases. </p>

                <p>Send a POST request to https://cardlist.co/new-order with the following JSON body structure:</p>
                <CodeBlock
                text={
                    JSON.stringify(
                        {
                            "items" : [

                                {
                                    "name": "Coffee",
                                    "price" : 500
                                },
                                {
                                    "name": "Everything Bagel",
                                    "price" : 460
                                }

                                ],
                            "subtotal": 960,
                            "shipping": 0,
                            "taxes": 0,
                            "total": 960,
                            "storename": "Cafe Adam",
                            "acctID": "acct_1IXBaIICjuu6vzYp",
                            "returnURL" : "https://example.com/order/2",
                            "successURL" : "https://example.com/succcess/2"

                        }
                    ,null, 1)

                }
                language={"json"}
                showLineNumbers={true}

                />
            </div>
            <div>
               </div>
        </>


    )
}

export default Home
