import React , {useEffect, useState} from 'react';
import { useParams} from "react-router-dom";


const Tap = () => {
    const {id} = useParams();
    const {items, setItems} = useState([]);

    useEffect( () => {
        var decodedStringAtoB = atob(id);
        var body = JSON.parse(decodedStringAtoB);

        setItems([body.items,body.acctID]);

       },[items]);

    return (
        <>

             <div><h1>TapPay</h1></div>
             <h2>Order Details</h2>
             <p>{items}</p>



        </>
    )
}

export default Tap;
